    [Fact]
    public async Task DtekOutagesHandler_ConfigureOutages_ReturnsLessons()
    {
        // Arrange
        var mockHttpFactory = new MockHttpFactory().Setup(_normalData);
        var dtekHandler = new DtekOutagesHandler(mockHttpFactory);
        await dtekHandler.InitializeOutages();
        const int actualNumberOfOutages = 2;
